mapper.update:
update t_daily_practice_answer
daily_practice_id = #{dailyPracticeId,jdbcType=INTEGER}, user_id = #{userId,jdbcType=INTEGER}, practice_date = #{practiceDate,jdbcType=DATE}, question_ids = #{questionIds,jdbcType=VARCHAR}, answer_content = #{answerContent,jdbcType=VARCHAR}, question_count = #{questionCount,jdbcType=INTEGER}, question_correct = #{questionCorrect,jdbcType=INTEGER}, score = #{score,jdbcType=INTEGER}, do_time = #{doTime,jdbcType=INTEGER}, status = #{status,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=INTEGER}

mapper.select:
select

from t_daily_practice_answer
where daily_practice_id = #{dailyPracticeId,jdbcType=INTEGER}
and user_id = #{userId,jdbcType=INTEGER}
and practice_date = #{practiceDate,jdbcType=DATE}

select

from t_daily_practice_answer
where daily_practice_id = #{dailyPracticeId,jdbcType=INTEGER}
and user_id = #{userId,jdbcType=INTEGER}
and practice_date = #{practiceDate,jdbcType=DATE}
order by score desc
limit 1

select count(*)
from t_daily_practice_answer
where daily_practice_id = #{dailyPracticeId,jdbcType=INTEGER}
and user_id = #{userId,jdbcType=INTEGER}
and practice_date = #{practiceDate,jdbcType=DATE}

select
from t_daily_practice_answer where id in ( select min(a.id) from t_daily_practice_answer a inner join ( select daily_practice_id, user_id, practice_date, max(score) as max_score from t_daily_practice_answer
and daily_practice_id = #{dailyPracticeId}
group by daily_practice_id, user_id, practice_date ) b on a.daily_practice_id = b.daily_practice_id and a.user_id = b.user_id and a.practice_date = b.practice_date and a.score = b.max_score
and a.daily_practice_id = #{dailyPracticeId}
group by a.daily_practice_id, a.user_id, a.practice_date ) order by id desc
